import React, {useCallback, useEffect, useRef, useState} from 'react';
import './MedicineList.css';
import AdminPageHeader from "../../AdminPageHeader";

import * as MedicineAPI from "../../api/Medicine";
import {Message, Table} from "../../../component";
import * as CommonJs from "../../../lib/Common";
import {Link, useLocation, useNavigate} from "react-router-dom";
import {PlusCircleOutlined} from "@ant-design/icons";
import {Image} from "antd";
import * as OrderAPI from "../../api/Order";

const MedicineList = function (props) {
    const urlPrefix = '/admin/medicine';
    const location = useLocation();
    const navigate = useNavigate();
    const table = useRef();
    const [total, setTotal] = useState(0);

    const columns = [
        {
            title: '번호',
            dataIndex: 'seq',
            key: 'seq',
            render: (text, record, index) => {
                console.log(record);
                return record.rownum
            },

        },
        {
            title: '썸네일',
            dataIndex: '',
            key: '',
            render: (_, record) => {
                return <a href={"/admin/medicine/detail/"+record['seq']}>
                    <Image
                        width={200}
                        height={200}
                        src= {(record.hasOwnProperty('fileList') && record.fileList.length > 0 ? record.fileList[0].fullFilePath : 'data:image/png;base64,') || "error"}
                        fallback="data:image/png;base64,"
                    />
                </a>
            }
        },
        {
            title: '제품명',
            dataIndex: 'productName',
            key: 'productName',
            render: (_, record) => <Link to={'/admin/medicine/detail/' + record.seq}>{record.productName}</Link>,
        },
        {
            title: '',
            dataIndex: 'unit',
            key: 'unit',
        },
        {
            title: '가격',
            dataIndex: 'buyerTypeProductAmount.amount',
            key: 'buyerTypeProductAmount.amount',
            render: (_, record) => {
                return (record.buyerTypeProductAmountList[0].amount != null ? (record.buyerTypeProductAmountList[0].amount).toLocaleString() : '') + "원"
            }
        },
        {
            title: '재고',
            dataIndex: 'quantity',
            key: 'quantity',
            render: (_, record) => {
                if(record.quantity < 10){
                    return <font style={{color:"red"}}>{record.quantity.toLocaleString()}</font>;
                }
                return record.quantity.toLocaleString();
            }
        },
        {
            title: '제조사',
            dataIndex: 'factory',
            key: 'factory',
        },
        {
            title: '사용',
            dataIndex: 'useYn',
            key: 'useYn',
        },
    ];
    const filters = [
        {
            key: "searchConditionSelect",
            name: "",
            type: "select",
            options:[
                {
                    "value":"",
                    "name":"전체"
                },
                {
                    "value":"standardCode",
                    "name":""
                },
                {
                    "value":"productName",
                    "name":"제품명"
                },
                {
                    "value":"factory",
                    "name":"제조사"
                },
                {
                    "value":"useYn",
                    "name":"사용여부"
                }
            ]
        },
        {
            key: "searchConditionText",
            name: "",
            type: "text",
        }
    ];
    const onSearch = (params) => {
        let param = CommonJs.jsonToQueryString(params);
        navigate(urlPrefix + '/list' + param);
        table.current['onSubmit']();
    }
    const onClickAddButton = useCallback(() => {
        navigate(urlPrefix + "/add" + location.search);
    }, [props]);
    const buttons = [
        // {
        //     type: 'primary',
        //     disabled: false,
        //     name: '상품목록 다운로드(.xls)',
        // },
        {
            type: 'primary',
            disabled: false,
            name: '등록',
            icon: <PlusCircleOutlined/>,
            onClick: onClickAddButton,
        },
    ];

    useEffect(() => {
        MedicineAPI.list({
            pageNo: 1,
            len: 2147483647
        })
            .then((response) => {
                const res = response.data;
                const total = res.totalElements || 0;
                console.log(response);
                setTotal(total);
            })
            .catch((error) => {
                Message.error(error.message);
            })
    }, [])

    return (
        <div className="buyer-list-layout">
            <AdminPageHeader title={"상품관리"} subTitle={"-목록"}/>
            <p style={{float:"left"}}>총 {total}건</p>
            <Table
                buttons={buttons}
                columns={columns}
                filters={filters}
                onSearch={onSearch}
                isInitLoad={true}
                onList={MedicineAPI.list}
                innerRef={table}
            />
        </div>
    );
};

export default MedicineList;
