import React, {useCallback, useEffect, useState} from 'react';
import 'antd/dist/antd.css';
import {Button, Card, Col, Divider, Image, InputNumber, Layout, Popconfirm, Row, Steps, Tabs} from 'antd';
import {useNavigate, useParams} from "react-router-dom";
import {MinusOutlined, PlusOutlined, ShoppingCartOutlined} from '@ant-design/icons';
import {Message} from "../../component";
import * as MedicineAPI from "../../admin/api/Medicine";
import './BuyerMedicineDetail.css';
import * as OrderAPI from "../../admin/api/Order";

const {TabPane} = Tabs;

const BuyerMedicineDetail = function () {
    const [detailData, setDetailData] = useState([]);
    const [isResult, setIsResult] = useState(false);
    const [quantity, setQuantity] = useState(1);
    const [productType1, setProductType1] = useState(1);
    const [productType2, setProductType2] = useState(1);
    const navigate = useNavigate();
    const params = useParams();
    const id = Number(params.id);
    const _onList = () => {
        const values = {
            map: {
                seq: id
            }
        };
        MedicineAPI.buyerGet(values)
            .then((res) => {
                console.log(res);
                if(res.code !== 1000){
                    alert("구매 가능한 상품이 아닙니다.")
                    window.history.back();
                    return;
                }

                setDetailData(res.data);
                setProductType1((res.data.insuranceDivision === "1" ? false : true));
                setProductType2((res.data.specialDivision === "1" ? false : true));
                setIsResult(true);
            })
            .catch((err) => {
                // Message.error(err.message);
                alert("구매 가능한 상품이 아닙니다.");
            });
    }
    useEffect(() => {
        _onList();
    }, [setDetailData]);
    const setCart = () => {
        OrderAPI.setCart({
            map: {
                quantity: quantity,
                productSeq: id
            }
        }).then(res => {
            console.log(res);
        }).catch(err => {
            Message.error(err);
        })
    };
    const onConfirm = useCallback(() => {
        navigate("/buyer/cart");
    }, []);

    const goOrder = () => {
        navigate('/buyer/order', {
            state: {
                quantity: quantity,
                productSeq: id
            }
        })
    }
    const changeQuantity = (values) => {
        setQuantity(values);
    }
    const changeQuantityPlusOne = () => {
        setQuantity(quantity + 1);
    }
    const changeQuantityMinusOne = () => {
        setQuantity(quantity - 1);
    }

    return (
        <div>
            {
                isResult &&
                <div className="medicine-detail-container">
                    <Row>
                        <Col span={"12"}>
                            <Image
                                width={500}
                                src= {(detailData.hasOwnProperty('fileList') && detailData.fileList.length > 0 ? detailData.fileList[0].fullFilePath : 'data:image/png;base64,') || "error"}
                                fallback="data:image/png;base64,"
                            />
                        </Col>
                        <Col span={"12"}>
                            <div className="medicine-detail-description">
                                <div className="medicine-detail-button medicineTag">
                                    {
                                        productType1 &&
                                            <span class="tag01"></span>
                                    }
                                    {
                                        !productType1 &&
                                            <span class="tag02"></span>
                                    }
                                    {
                                        productType2 &&
                                            <span class="tag03"></span>
                                    }
                                </div>
                                <div className="productName">{detailData['productName']}</div>
                                <div className="ingredientName">{detailData['ingredientName']}</div>
                                <div className="priceGroup">
                                    <div className="price">{detailData.buyerTypeProductAmountList[0].amount.toLocaleString()} 원</div>
                                    <div className="quantity">구매수량 <InputNumber value={quantity} min={1} onChange={changeQuantity}/></div>
                                </div>
                                <div className="btnGroup">
                                    <p className="buy">
                                        <Button type="primary" className='btn-color02 btn-bold' onClick={goOrder} block>바로 구매 -
                                        ￦ {(detailData.buyerTypeProductAmountList[0].amount * quantity).toLocaleString()}원</Button>
                                    </p>
                                    <p className="cart">
                                        <Popconfirm placement="topLeft" title={'장바구니에 상품을 담았습니다. 장바구니로 이동하시겠습니까?'}
                                                    onClick={setCart} onConfirm={onConfirm} okText="확인" cancelText="취소">
                                            <Button className='btn-color02-2' icon={<ShoppingCartOutlined/>} block/>
                                        </Popconfirm>
                                    </p>
                                </div>
                                <dl className="medicine-detail-info">
                                    <dt className="column"></dt><dd className="data font-weight-600">{detailData['factory']}</dd>
                                    <dt className="column"></dt><dd className="data">{detailData['standard']}</dd>
                                    <dt className="column"></dt><dd className="data">{
                                    (detailData['specialtyDivision'] !== "0" ?
                                        (detailData['specialtyDivision'] !== "2" ?
                                            (detailData['specialtyDivision'] !== "1" ?
                                                "" : '')
                                        : '')
                                    : '')
                                    }</dd>
                                    <dt className="column"></dt><dd className="data">{detailData['formulaDivision']
                                    }</dd>
                                </dl>
                            </div>
                        </Col>
                    </Row>
                    <Row>
                        <Tabs className={"table-center medicine-detail-detailInfo"} size="large" tabBarGutter={200} defaultActiveKey="1"
                              centered>
                            <TabPane tab="상세정보" key="1">
                                <div dangerouslySetInnerHTML={{__html: detailData['productDetail']}}></div>
                            </TabPane>
                            <TabPane tab="배송/교환/반품정보" key="2">
                                배송/교환/반품 안내
                            </TabPane>
                        </Tabs>
                    </Row>
                </div>
            }
        </div>
    );
};

export default BuyerMedicineDetail;